import { FormControl, InputLabel, MenuItem, Select } from '@mui/material';
import React, { useState } from 'react';
import classes from './css/ProductFilters.module.css';

const ProductFilters = ({ filters, onChange, list }) => {
  const [value, setValue] = useState('');

  const onChangeHandler = (event) => {
    onChange(id, event.target.value);
  };

  return (
    <span className={classes['form-div']}>
      {filters.map((filter) => (
        <FormControl key={filter.name} variant="outlined" className={classes['form-control']}>
          <InputLabel id={`${filter.name}-label`}>{filter.name}</InputLabel>
          <Select labelId={`${filter.name}-label`} displayEmpty id={filter.name} value={value} onChange={onChangeHandler} sx={{ textAlign: 'right' }}>
            <MenuItem value="">전체</MenuItem>
            {filter.list?.map((menu_item) => (
              <MenuItem key={menu_item.value} value={menu_item.value}>
                {menu_item.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      ))}
    </span>
  );
};

export default ProductFilters;
