import React from "react";
import { Link } from "react-router-dom";
import './Home.css'; // 스타일링을 위한 CSS 파일을 추가할 수 있습니다.

const Home = () => {
  return (
    <div className="home-container">
      {/* 상단 배너 */}
      <div className="banner">
        <img src="배너이미지URL" alt="쇼핑몰 배너" />
      </div>

      {/* 카테고리 메뉴 */}
      <div className="category-menu">
        <Link to="/category/men"><div>남성 의류</div></Link>
        <Link to="/category/women"><div>여성 의류</div></Link>
        <Link to="/category/accessories"><div>액세서리</div></Link>
      </div>

      {/* 추천 상품 섹션 */}
      <div className="featured-products">
        <h2>추천 상품</h2>
        <div className="product-list">
          <div className="product-item">
            <img src="상품이미지URL1" alt="상품1" />
            <div>상품 이름 1</div>
            <div>₩10,000</div>
          </div>
          <div className="product-item">
            <img src="상품이미지URL2" alt="상품2" />
            <div>상품 이름 2</div>
            <div>₩20,000</div>
          </div>
          {/* 더 많은 추천 상품 */}
        </div>
      </div>

      {/* 로그인/회원가입 링크 */}
      <div className="auth-links">
        <Link to='/sign-in'><div>로그인</div></Link>
        <Link to='/sign-up'><div>회원가입</div></Link>
      </div>

      {/* 상품 목록 링크 */}
      <div className="product-list-link">
        <Link to='/prod-list'><div>전체 상품 보러가기</div></Link>
      </div>
    </div>
  );
}

export default Home;
