import { useContext } from 'react';
import { Link } from 'react-router-dom';

import CartContext from '../../../../store/cart-context';
import ProductItemForm from './ProductItemForm';
import classes from './css/ProductItem.module.css';

const ProductItem = (props) => {
	const cartCtx = useContext(CartContext);
	const price = `${props.price} 원`;

	const addToCartHandler = (amount) => {
		cartCtx.addItem({
			id: props.id,
			name: props.name,
			amount: amount,
			price: props.price,
		});
	};

	return (
		<li className={classes.product}>
			<div>
				<h3>{props.name}</h3>
				{props.productInfo && <div className={classes.description}>{props.productInfo}</div>}
				<div>
					<Link>
						<img src={props.img} alt={props.name} />
					</Link>
				</div>
				<div className={classes.price}>
					{/* 원가와 할인 가격 표시 */}
					{props.rprice > 0 ? (
						<>
							<div className={classes.originalPrice}>
								{props.oprice.toLocaleString()} 원
							</div>
							<div className={classes.discount}>
								{/* 할인율 계산 */}
								{Math.round(((props.oprice - props.rprice) / props.oprice) * 100)}% OFF
								<span className={classes.salePrice}>{props.rprice.toLocaleString()} 원</span>
							</div>
						</>
					) : (
						<div className={classes.originalPrice}>
							{props.oprice.toLocaleString()} 원
						</div>
					)}
				</div>
			</div>
			<div>
				<ProductItemForm id={props.id} onAddToCart={addToCartHandler} />
			</div>
		</li>
	);
};

export default ProductItem;
