import React, { useState } from 'react';
import ProductFilter from './ProductFilter';

const ProductForm = () => {
	const [filterMethod, setFilterMethod] = useState('');

	const onSelectChange = (event) => {
		setFilterMethod(event.target.value);

		if (event.target.value && event.target.value !== '') {
			fetchProductsHandlerByGenreCode(event.target.value);
		}
	};

	return (
		<ProductFilter
			props={{
				filter_name: '장르 대분류 선택',
				menu_list: [
					{ value: '001', label: '상의' },
					{ value: '002', label: '아우터' },
					{ value: '003', label: '바지' },
					{ value: '004', label: '원피스' },
					{ value: '005', label: '스커트' },
					{ value: '006', label: '스니커즈' },
					{ value: '007', label: '신발' },
					{ value: '008', label: '모자' },
					{ value: '009', label: '양말 및 레그워머' },
				],
			}}
		></ProductFilter>
	);
};

export default ProductForm;
