import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import styles from './css/ProductDetail.module.css';
import ApiService from '../../../../ApiService';

const ProductDetail = () => {
    const { productNum } = useParams(); // URL 파라미터에서 productNum 가져오기
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchProductDetails = async () => {
            try {
                const response = await ApiService.fetchProductByNum(productNum);
                const data = response.data;
                setProduct(data);
            } catch (error) {
                console.error("Error fetching product details:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchProductDetails();
    }, [productNum]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (!product) {
        return <div>상품을 찾을 수 없습니다.</div>;
    }

    const isDiscounted = product.rprice > 0 && product.rprice < product.oprice;
    const discountPercentage = isDiscounted 
        ? Math.round(((product.oprice - product.rprice) / product.oprice) * 100)
        : 0;

    return (
        <div className={styles.productDetail}>
            {/* 헤더 */}
            <div className={styles.header}>
                <button className={styles.backButton} onClick={() => window.history.back()}>← 목록으로</button>
            </div>

            {/* 이미지 캐러셀 */}
            <div className={styles.imageCarousel}>
                {product.productImg.map((img, index) => (
                    <img key={index} src={img} alt={product.productName} className={styles.productImage} />
                ))}
            </div>

            {/* 상품 정보 */}
            <div className={styles.productInfo}>
                <h1 className={styles.productName}>{product.productName}</h1>
                <a href="#reviews" className={styles.reviewLink}>리뷰 바로가기 ({product.reviews.length})</a>
                
                {/* 가격 정보 */}
                <p className={styles.priceInfo}>
                    {isDiscounted ? (
                        <>
                            <span className={styles.originalPrice}>{product.oprice.toLocaleString()} 원</span>
                            <span className={styles.discountPrice}>{product.rprice.toLocaleString()} 원</span>
                            <span className={styles.discountPercentage}>({discountPercentage}% 할인)</span>
                        </>
                    ) : (
                        <span>{product.oprice.toLocaleString()} 원</span>
                    )}
                </p>

                <p className={styles.brand}>브랜드: {product.productBrand}</p>
                <p className={styles.category}>카테고리: {product.category.join(', ')}</p>
            </div>

            {/* 상품 상세 정보 */}
            <div className={styles.productDetails}>
                <h2>상세 정보</h2>
                <ul>
                    {product.productDetails.map((detail, index) => (
                        <li key={index}>
                            <strong>사이즈:</strong> {detail.productSize}, 재고: {detail.productInven}개
                        </li>
                    ))}
                </ul>
            </div>

            {/* 리뷰 */}
            <div className={styles.reviews} id="reviews">
                <h2>리뷰</h2>
                {product.reviews.length > 0 ? (
                    product.reviews.map((review) => (
                        <div key={review.reviewNum} className={styles.review}>
                            <h3>{review.reviewTitle}</h3>
                            <p>{review.reviewContent}</p>
                        </div>
                    ))
                ) : (
                    <p>아직 작성된 리뷰가 없습니다.</p>
                )}
            </div>

            {/* 푸터 */}
            <div className={styles.footer}>
                <div className={styles.footerButtons}>
                    <button className={styles.favoriteButton}>❤ 관심상품 등록</button>
                    <button className={styles.purchaseButton}>구매하기</button>
                </div>
            </div>
        </div>
    );
};

export default ProductDetail;
