import React, { useState, useEffect } from 'react';
import { Button, Typography, Rating, TextField, Tabs, Tab, Box, Breadcrumbs, Link, Chip, Grid, Container } from '@mui/material';
import { ShoppingCart, Favorite, Compare } from '@mui/icons-material';
import ApiService from '../../../../ApiService';
import styles from './css/ProductDetail.module.css';

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div role="tabpanel" hidden={value !== index} id={`simple-tabpanel-${index}`} aria-labelledby={`simple-tab-${index}`} {...other}>
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

export default function ProductDetail() {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  const [tabValue, setTabValue] = useState(0);
  const [selectedColor, setSelectedColor] = useState('');
  const [selectedSize, setSelectedSize] = useState('');
  const [mainImage, setMainImage] = useState('');

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        // 실제 API 호출로 대체해야 합니다.
        // const response = await ApiService.fetchProductByNum(productNum);
        // const data = response.data;
        const data = {
          productCode: '12345',
          productName: '프리미엄 컴포트 후드티',
          productBrand: '컴포트웨어',
          oprice: 59000,
          rprice: 49000,
          rating: 4.5,
          reviewCount: 128,
          description: '최고의 편안함을 경험해보세요. 100% 유기농 면으로 제작된 이 후드티는 하루 종일 지속되는 통기성과 부드러움을 제공합니다.',
          features: ['100% 유기농 면', '통기성 좋은 원단', '강화된 스티칭', '다양한 색상 선택 가능', 'XS부터 XXL까지 다양한 사이즈'],
          productImg: [
            'https://images.unsplash.com/photo-1556821840-3a63f95609a7?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80',
            'https://images.unsplash.com/photo-1578587018452-892bacefd3f2?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80',
            'https://images.unsplash.com/photo-1586790170083-2f9ceadc732d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80',
          ],
          colors: ['화이트', '블랙', '네이비', '그레이'],
          sizes: ['XS', 'S', 'M', 'L', 'XL', 'XXL'],
        };
        setProduct(data);
        setMainImage(data.productImg[0]);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching product:', error);
        setLoading(false);
      }
    };

    fetchProduct();
  }, []);

  const handleQuantityChange = (event) => {
    setQuantity(Math.max(1, parseInt(event.target.value)));
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleColorSelect = (color) => {
    setSelectedColor(color);
  };

  const handleSizeSelect = (size) => {
    setSelectedSize(size);
  };

  const handleImageClick = (image) => {
    setMainImage(image);
  };

  if (loading) {
    return <Typography>로딩 중...</Typography>;
  }

  if (!product) {
    return <Typography>상품을 찾을 수 없습니다.</Typography>;
  }

  return (
    <Container maxWidth="lg" className={styles.productDetail}>
      <Breadcrumbs aria-label="breadcrumb" className={styles.breadcrumbs}>
        <Link color="inherit" href="/">
          홈
        </Link>
        <Link color="inherit" href="/category">
          의류
        </Link>
        <Typography color="text.primary">{product.productName}</Typography>
      </Breadcrumbs>

      <Grid container spacing={4}>
        <Grid item xs={12} md={6}>
          <div className={styles.imageContainer}>
            <img src={mainImage} alt={product.productName} className={styles.mainImage} />
          </div>
          {product.productImg.length > 1 && (
            <div className={styles.thumbnailContainer}>
              {product.productImg.map((img, index) => (
                <div
                  key={index}
                  className={`${styles.thumbnail} ${img === mainImage ? styles.thumbnailActive : ''}`}
                  onClick={() => handleImageClick(img)}
                >
                  <img src={img} alt={`${product.productName} 뷰 ${index + 1}`} className={styles.thumbnailImage} />
                </div>
              ))}
            </div>
          )}
        </Grid>
        <Grid item xs={12} md={6}>
          <div className={styles.productInfo}>
            <Typography variant="h4" component="h1" className={styles.productName}>
              {product.productName}
            </Typography>
            <Typography variant="subtitle1" className={styles.brandName}>
              브랜드: {product.productBrand}
            </Typography>
            <div className={styles.ratingContainer}>
              <Rating value={product.rating} precision={0.5} readOnly />
              <Typography variant="body2" className={styles.reviewCount}>
                (리뷰 {product.reviewCount}개)
              </Typography>
            </div>
            <div className={styles.priceContainer}>
              <span className={styles.discountPrice}>{product.rprice.toLocaleString()}원</span>
              <span className={styles.originalPrice}>{product.oprice.toLocaleString()}원</span>
            </div>
            <Typography variant="body1" className={styles.description}>
              {product.description}
            </Typography>

            <div className={styles.optionContainer}>
              <Typography variant="subtitle1" className={styles.optionTitle}>
                색상:
              </Typography>
              <div className={styles.optionChips}>
                {product.colors.map((color) => (
                  <Chip
                    key={color}
                    label={color}
                    onClick={() => handleColorSelect(color)}
                    color={selectedColor === color ? 'primary' : 'default'}
                    className={styles.optionChip}
                  />
                ))}
              </div>
            </div>

            <div className={styles.optionContainer}>
              <Typography variant="subtitle1" className={styles.optionTitle}>
                사이즈:
              </Typography>
              <div className={styles.optionChips}>
                {product.sizes.map((size) => (
                  <Chip
                    key={size}
                    label={size}
                    onClick={() => handleSizeSelect(size)}
                    color={selectedSize === size ? 'primary' : 'default'}
                    className={styles.optionChip}
                  />
                ))}
              </div>
            </div>

            <div className={styles.quantityContainer}>
              <TextField
                type="number"
                label="수량"
                value={quantity}
                onChange={handleQuantityChange}
                inputProps={{ min: 1 }}
                className={styles.quantityInput}
              />
              <Button variant="contained" startIcon={<ShoppingCart />} className={styles.addToCartButton}>
                장바구니
              </Button>
            </div>

            <div className={styles.actionButtons}>
              <Button startIcon={<Favorite />} variant="outlined" className={styles.wishlistButton}>
                찜하기
              </Button>
              <Button startIcon={<Compare />} variant="outlined" className={styles.compareButton}>
                비교
              </Button>
            </div>
          </div>
        </Grid>
      </Grid>

      <div className={styles.tabContainer}>
        <Tabs value={tabValue} onChange={handleTabChange} aria-label="상품 정보 탭">
          <Tab label="상세 설명" />
          <Tab label="특징" />
          <Tab label="리뷰" />
        </Tabs>
        <div className={styles.tabContent}>
          <TabPanel value={tabValue} index={0}>
            <Typography variant="body1">{product.description}</Typography>
          </TabPanel>
          <TabPanel value={tabValue} index={1}>
            <ul className={styles.featureList}>
              {product.features.map((feature, index) => (
                <li key={index} className={styles.featureItem}>
                  {feature}
                </li>
              ))}
            </ul>
          </TabPanel>
          <TabPanel value={tabValue} index={2}>
            <Typography variant="body1">고객 리뷰가 여기에 표시됩니다.</Typography>
          </TabPanel>
        </div>
      </div>
    </Container>
  );
}
