import React, { Component } from "react";
import ApiService from "../../ApiService";

import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

class EditBook extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: 0,
      uid: "",
      smartUid: "",
      name: "",
      bookNum : 100,
      borrower: "",
      bookCmp: 0,
      bookFloor: 0,
      donor: "",
      category: "",
      writer: "",
      count: 0,
      img: "",
    };
  }

  componentDidMount() {
    this.loadBook();
  }

  loadBook = () => {
    ApiService.fetchBookByID(window.localStorage.getItem("bookID"))
      .then((res) => {
        let book = res.data;
        this.setState({
          id: book.id,
          uid: book.uid,
          smartUid: book.smartUid,
          name: book.name,
          bookNum : book.bookNum,
          borrower: book.borrower,
          bookCmp: book.bookCmp,
          bookFloor: book.bookFloor,
          donor: book.donor,
          category: book.category,
          writer: book.writer,
          count: book.count,
          img: book.img,
        });
      })
      .catch((err) => {
        console.log("loadBook() 에러", err);
      });
  };

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  saveBook = (e) => {
    e.preventDefault();

    let book = {
      id: this.state.id,
      uid: this.state.uid,
      smartUid: this.state.smartUid,
      name: this.state.name,
      bookNum : this.state.bookNum,
      borrower: this.state.borrower,
      bookCmp: this.state.bookCmp,
      bookFloor: this.state.bookFloor,
      donor: this.state.donor,
      category: this.state.category,
      writer: this.state.writer,
      count: this.state.count,
      img: this.state.img,
    };

    ApiService.editBook(book)
      .then((res) => {
        console.log(book.name + "님 정보가 수정되었습니다.");
        this.props.history.push("/books");
      })
      .catch((err) => {
        console.log("saveBook() 에러", err);
      });
  };

  render() {
    return (
      <div>
        <Typography variant="h6" style={style}>
          도서 편집
        </Typography>

        <form>
          <TextField
            type="text"
            name="bookname"
            readOnly={true}
            fullWidth
            margin="normal"
            value={this.state.bookname}
          />

          <TextField
            placeholder="Edit your first name"
            name="firstName"
            fullWidth
            margin="normal"
            value={this.state.firstName}
            onChange={this.onChange}
          />

          <TextField
            placeholder="Edit your last name"
            name="lastName"
            fullWidth
            margin="normal"
            value={this.state.lastName}
            onChange={this.onChange}
          />

          <TextField
            type="number"
            placeholder="Edit your age"
            name="age"
            fullWidth
            margin="normal"
            value={this.state.age}
            onChange={this.onChange}
          />

          <TextField
            type="number"
            placeholder="Edit your salary"
            name="salary"
            fullWidth
            margin="normal"
            value={this.state.salary}
            onChange={this.onChange}
          />

          <Button variant="contained" color="primary" onClick={this.saveBook}>
            저장
          </Button>
        </form>
      </div>
    );
  }
}

const style = {
  display: "flex",
  justifyContent: "center",
};

export default EditBook;
