import { FormControl, InputLabel, MenuItem, Select } from '@mui/material';
import React, { useState } from 'react';

const ProductFilter = ({ id, item, onChange }) => {
  const [value, setValue] = useState('');

  const onChangeHandler = (event) => {
    onChange(id, event.target.value);
  };

  return (
    <FormControl variant="outlined" fullWidth>
      <InputLabel id={`${item[0]?.name}-label`}>{item[0]?.name}</InputLabel>
      <Select labelId={`${item[0]?.name}-label`} displayEmpty id={item[0]?.name} value={value} onChange={onChangeHandler} sx={{ textAlign: 'right' }}>
        <MenuItem value="">전체</MenuItem>
        {item[0]?.list.map((menu_item) => (
          <MenuItem key={menu_item.value} value={menu_item.value}>
            {menu_item.label}
          </MenuItem>
        ))}
      </Select>
    </FormControl>
  );
};

export default ProductFilter;
