import React, { useState, useEffect, useContext, useRef } from 'react';
import { Link } from "react-router-dom";
import { useNavigate } from 'react-router';
import ApiService from '../../ApiService';
import AuthContext from '../../store/auth-context';
import Loading from '../basic/Loading';
import { IconButton, InputAdornment, TextField, Typography, Button, Box, Card, CardMedia, FormControl, RadioGroup, FormControlLabel } from '@mui/material';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import ausercss from './css/auser.module.css';
import lightLogo from '../../img/OtPishAI_light.png';
import GoogleIcon from '@mui/icons-material/Google';

const SignUp = () => {
  document.body.style.backgroundColor = '#f0f8f9';

  const navigate = useNavigate();
  const ctx = useContext(AuthContext);

  const [user, setUser] = useState({});
  const [ageIsValid, setAgeIsValid] = useState(true);
  const [genderIsValid, setGenderIsValid] = useState(true);
  const [phoneIsValid, setPhoneIsValid] = useState(true);
  const [formIsValid, setFormIsValid] = useState(true);

  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    ctx.setCurrentPage('add-user');
  }, []);

  useEffect(() => {
    const identifier = setTimeout(() => {
      setFormIsValid(ageIsValid && genderIsValid && phoneIsValid);
    }, 500);

    return () => {
      clearTimeout(identifier);
    };
  }, [ageIsValid, genderIsValid, phoneIsValid]);

  const onChangeHandler = (event) => {
    setUser({
      ...user,
      [event.target.name]: event.target.value,
    });
  };
  
  const validateAgeHandler = () => {
    setIdIsValid(user.age !== undefined);
  };
  const validateGenderHandler = () => {
    setIdIsValid(user.gender !== '');
  };
  const validatePhoneHandler = () => {
    setPwIsValid(user.phone !== '');
  };

  const ageInputRef = useRef();
  const genderMaleInputRef = useRef();
  const genderFemaleInputRef = useRef();
  const genderEtcInputRef = useRef();
  const phoneInputRef = useRef();

  const addUserHandler = async (event) => {
    event.preventDefault();
    setIsLoading(true);
    setError(null);

    if (formIsValid) {
      const formData = new FormData();
      try {
        const response = await ApiService.addUser(formData);
        if (response.status < 200 || response.status > 299) {
          throw new Error('Something went wrong!');
        }
        navigate('/users');
      } catch (error) {
        setError(error.message);
      }
      setIsLoading(false);
    }
    
    if (!ageIsValid) {
      ageInputRef.current.focus();
    } else if (!genderIsValid) {
      genderMaleInputRef.current.focus();
    } else if (!phoneIsValid) {
      phoneInputRef.current.focus();
    }
  };

  let content = (
    <Box className={ausercss.box}>
      <form className={ausercss.form} onSubmit={addUserHandler}>
      <Card className={`${ausercss.card} ${ausercss.cardHeader}`}>
        <CardMedia
          className={ausercss.logo}
          component="img"
          image={lightLogo}
          alt="OtpishAI Light Logo"
        />
      </Card>
      <Card className={`${ausercss.card} ${ausercss.cardHalf} ${ausercss.cardLeft}`}>
        <Typography className={ausercss.typo} variant="h4">
          로그인
        </Typography>
        <Box
          variant='outlined'
          sx={{
            padding: '0 1em',
            width: 'fit-content',
            height: 'fit-content',
            borderColor: '#12b8de',
            border: 'none',
            borderLeft: '4px solid #12b9de6b',
          }}
        >
          <Typography className={`${ausercss.typo} ${ausercss.primaryDarkerFont}`} variant="h6">
            Google 계정 사용
          </Typography>
        </Box>
      </Card>
        <Card className={`${ausercss.card} ${ausercss.cardHalf} ${ausercss.cardRight}`}>
          <div>
            <TextField
              autoFocus
              type="number"
              name="age"
              label="나이"
              error={!ageIsValid}
              helperText={ageIsValid ? '' : '필수 작성란입니다.'}
              ref={ageInputRef}
              sx={{ m: 1, width: '55ch' }}
              variant="outlined"
              value={user.age || ''}
              onChange={onChangeHandler}
              onBlur={validateAgeHandler}
            />
          </div>
          <div>
            <FormControl>
              <RadioGroup
                aria-labelledby="demo-radio-buttons-group-label"
                defaultValue="female"
                name="gender"
              >
                <FormControlLabel 
                  type="radio"
                  name="gender"
                  label="성별"
                  error={!genderMaleInputRef}
                  helperText={genderIsValid ? '' : '필수 작성란입니다.'}
                  ref={genderMaleInputRef}
                  sx={{ m: 1, width: '55ch' }}
                  variant="outlined"
                  value="male"
                  onChange={onChangeHandler}
                  onBlur={validateGenderHandler}
                />
                <FormControlLabel 
                  type="radio"
                  name="gender"
                  label="성별"
                  error={!genderIsValid}
                  helperText={genderIsValid ? '' : '필수 작성란입니다.'}
                  ref={genderFemaleInputRef}
                  sx={{ m: 1, width: '55ch' }}
                  variant="outlined"
                  value="female"
                  onChange={onChangeHandler}
                  onBlur={validateGenderHandler}
                />
                <FormControlLabel 
                  type="radio"
                  name="gender"
                  label="성별"
                  error={!genderIsValid}
                  helperText={genderIsValid ? '' : '필수 작성란입니다.'}
                  ref={genderEtcInputRef}
                  sx={{ m: 1, width: '55ch' }}
                  variant="outlined"
                  value="etc"
                  onChange={onChangeHandler}
                  onBlur={validateGenderHandler}
                />
              </RadioGroup>
            </FormControl>
          </div>
          <div>
            <TextField
              type="text"
              name="phone"
              label="핸드폰번호"
              error={!phoneIsValid}
              helperText={phoneIsValid ? '' : '필수 작성란입니다.'}
              ref={phoneInputRef}
              sx={{ m: 1, width: '55ch' }}
              variant="outlined"
              value={user.phone || ''}
              onChange={onChangeHandler}
              onBlur={validatePhoneHandler}
            />
          </div>
        </Card>
        <Card className={`${ausercss.card} ${ausercss.cardFooter}`}>
          <Card className={`${ausercss.card} ${ausercss.cardHalf}`}>
          </Card>
          <Card className={`${ausercss.card} ${ausercss.cardHalf}`}>
            <Button
              className={ausercss.addBtn}
              variant="contained"
              type="submit"
            >
              다음
            </Button>
          </Card>
        </Card>
      </form>
    </Box>
  );
  if (error) {
    content = (
      <div>
        <p>{error}</p>
      </div>
    );
  }
  if (isLoading) {
    content = <Loading />;
  }

  return (
    <>
      {content}
    </>
  );
};

export default SignUp;
