import { FormControl, InputLabel, MenuItem, Select } from '@mui/material';
import React, { useState } from 'react';
import classes from './css/ProductFilters.module.css';

const ProductFilters = ({ id, renderItems, onChange }) => {
  const majorKey = renderItems.items?.find((item) => item.id?.includes('major'));
  const minorKey = renderItems.items?.find((item) => item.id?.includes('minor'));
  const minorObj = renderItems.items.find((item) => item.id === minorKey);

  const [valueObj, setValueObj] = useState(renderItems.reduce((acc, filter) => ({ ...acc, [filter.id]: '' }), {}));
  const [filterList, setFilterList] = useState(minorObj.listobj || minorObj.list);

  const onChangeHandler = (event, filter_id) => {
    const param = { ...valueObj, [filter_id]: event.target.value };
    setValueObj(param);

    valueObj[majorKey] && filter_id.includes('minor') && onChange(id, Object.values(param)?.join('0'));
  };

  return (
    <div className={classes['form-div']} style={{ marginBottom: '1em' }}>
      {renderItems.map((filter) => (
        <FormControl key={filter.id} variant="outlined" className={classes['form-control']}>
          <InputLabel id={`${filter.id}-label`}>{filter.name}</InputLabel>
          <Select
            labelId={`${filter.id}-label`}
            displayEmpty
            id={filter.id}
            value={valueObj[filter.id]}
            onChange={(event) => onChangeHandler(event, filter.id)}
            sx={{ textAlign: 'right' }}
          >
            <MenuItem value="">전체</MenuItem>
            {filterList?.map((menu_item) => (
              <MenuItem key={menu_item.value} value={menu_item.value}>
                {menu_item.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      ))}
    </div>
  );
};

export default ProductFilters;
