import { FormControl, InputLabel, MenuItem, Select } from '@mui/material';
import React, { useState } from 'react';
import classes from './css/ProductFilters.module.css';

const ProductFilters = ({ id, filters, onChange, list }) => {
  const [valueObj, setValueObj] = useState(
    filters.reduce((acc, filter) => {
      return acc ? { ...acc, [filter.id]: '' } : { [filter.id]: '' };
    },
    {}
  ));

  const onChangeHandler = (event) => {
    const major_key = Object.keys(valueObj)?.find((key) => key.includes('major'));
    const { item_id, item_value } = event.target.value;

    const param = { ...valueObj, [item_id]: item_value };
    setValueObj(param);

    valueObj[major_key] && item_id.includes('minor') && onChange(id, Object.values(param.join('')));
  };

  return (
    <span className={classes['form-div']}>
      {filters.map((filter) => (
        <FormControl key={filter.id} variant="outlined" className={classes['form-control']}>
          <InputLabel id={`${filter.id}-label`}>{filter.name}</InputLabel>
          <Select
            labelId={`${filter.id}-label`}
            displayEmpty
            id={filter.id}
            value={valueObj[filter.id]}
            onChange={onChangeHandler}
            sx={{ textAlign: 'right' }}
          >
            <MenuItem value="">전체</MenuItem>
            {filter.list?.map((menu_item) => (
              <MenuItem key={menu_item.value} value={{ item_id: filter.id, item_value: menu_item.value }}>
                {menu_item.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      ))}
    </span>
  );
};

export default ProductFilters;
