import { useContext } from 'react';
import { Link } from 'react-router-dom';

import CartContext from '../../../../store/cart-context';
import ProductItemForm from './ProductItemForm';
import classes from './css/ProductItem.module.css';

const ProductItem = (props) => {
	const cartCtx = useContext(CartContext);
	const price = `${props.price} 원`;
	const discountPercentage = props.oprice > 0 && props.rprice > 0 ? Math.round(((props.oprice - props.rprice) / props.oprice) * 100) : 0;

	const addToCartHandler = (amount) => {
		cartCtx.addItem({
			id: props.id,
			name: props.name,
			amount: amount,
			price: props.price,
		});
	};

	return (
		<li className={classes.product}>
			<div>
				<h3>{props.name}</h3>
				<div className={classes.description}>{props.genre}</div>
				<div>
					<Link>
						<img src={props.img} alt={props.name} />
					</Link>
				</div>
				<div className={classes['price-info']}>
					{props.rprice > 0 && (
						<>
							<span className={classes.originalPrice}>{props.oprice} 원</span>
							<span className={classes.discountPercentage}>{discountPercentage}%</span>
						</>
					)}
					<span className={classes.price}>{props.rprice > 0 ? props.rprice : props.oprice} 원</span>
				</div>
			</div>
			<div>
				<ProductItemForm id={props.id} onAddToCart={addToCartHandler} />
			</div>
		</li>
	);
};

export default ProductItem;
