import SmartToyIcon from '@mui/icons-material/SmartToy';
import IconButton from '@mui/material/IconButton';
import { useEffect, useState, useCallback } from 'react';
import Sidebar from '../basic/SideBar';
import Header from '../layout/Header';
import Cart from './cart/Cart';
import classes from './css/ProdList.module.css';
import Products from './products/Products';
import ApiService from '../../ApiService';

function ProdList() {
  const [cartIsShown, setCartIsShown] = useState(false);
  const [sidebarIsVisible, setSidebarIsVisible] = useState(false);
  const [htmlContent, setHtmlContent] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [query, setQuery] = useState({
    genre_major: '',
    genre_minor: '',
    genre: '',
    brand: '',
    category: '',
    sort: 'payment_n,desc',
    page: 1,
  });

  useEffect(() => {
    if (sidebarIsVisible) {
      fetch('/chatbot.html')
        .then((response) => response.text())
        .then((data) => {
          const parser = new DOMParser();
          const doc = parser.parseFromString(data, 'text/html');
          const headElements = doc.head.querySelectorAll('style, link[rel="stylesheet"]');
          headElements.forEach((element) => {
            document.head.appendChild(element.cloneNode(true));
          });
          const bodyContent = doc.body.innerHTML;
          setHtmlContent(bodyContent);
          const scriptElements = doc.body.querySelectorAll('script');
          scriptElements.forEach((script) => {
            const newScript = document.createElement('script');
            if (script.src) {
              newScript.src = script.src;
            } else {
              newScript.textContent = script.textContent;
            }
            document.body.appendChild(newScript);
          });
        })
        .catch((error) => console.error('Failed to load chatbot.html:', error));
    }

    document.body.style.overflow = sidebarIsVisible ? 'hidden' : 'auto';

    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [sidebarIsVisible]);

  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const toggleSidebar = () => {
    setSidebarIsVisible((prev) => !prev);
  };

  const handleSearch = useCallback(async (keyword) => {
    try {
      const genre = query.genre
      let queryString = `keyword=${encodeURIComponent(keyword)}`;
      if (genre) {
        queryString += `&genre=${encodeURIComponent(genre)}`;
      }
      const response = await ApiService.searchProductsByQueryString(queryString);
      if (response.status === 200) {
        setSearchResults(response.data);
      }
    } catch (error) {
      console.error('Search failed:', error);
      setSearchResults(null);
    }
  }, []);

  const handleQueryChange = useCallback((newQuery) => {
    setQuery(newQuery);
  }, []);

  return (
    <>
      <Header 
        cartIsShown={cartIsShown} 
        setCartIsShown={setCartIsShown} 
        onShowCart={showCartHandler}
        onSearch={handleSearch}
        query={query}
        onQueryChange={handleQueryChange}
      />
      <main className={classes.main}>
        <Products searchResults={searchResults} query={query} onQueryChange={handleQueryChange} />
      </main>

      <IconButton
        onClick={toggleSidebar}
        sx={{
          position: 'fixed',
          right: '30px',
          bottom: '30px',
          width: '30px',
          height: '30px',
        }}
      >
        <SmartToyIcon color="primary" />
      </IconButton>

      <Sidebar
        isVisible={sidebarIsVisible}
        content={<div className={classes['sidebar-content']} dangerouslySetInnerHTML={{ __html: htmlContent }}></div>}
        onClose={toggleSidebar}
      />
    </>
  );
}

export default ProdList;
