import React, { useState, useEffect, useRef, useContext } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Typography,
  Button,
  IconButton,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Rating,
  Tabs,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Avatar,
  Chip,
  Box,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
} from '@mui/material';
import {
  ExpandMore,
  Favorite,
  FavoriteBorder,
  Share,
  Star,
  StarBorder,
  ShoppingCartOutlined,
  ThumbUpOutlined,
  VerifiedUser,
  Check,
  Wash,
  RemoveCircleOutline,
  Iron,
  DryCleaningOutlined,
  ChevronLeft,
  ChevronRight,
} from '@mui/icons-material';
import { TabPanel } from '../../../layout';
import Header from '../../../layout/Header';
import styles from './css/ProductDetail.module.css';
import ApiService from '../../../../ApiService';
import CartContext from '../../../../store/cart-context';
import { care, colors, details, fabricInfo, features, reviews, sizeChart, sizeGuide, sizes } from './data';

export default function ProductDetail() {
  const { productNum } = useParams();

  const [cartIsShown, setCartIsShown] = useState(false);
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedSize, setSelectedSize] = useState('');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [expandedMaterial, setExpandedMaterial] = useState(true);
  const [expandedCare, setExpandedCare] = useState(false);
  const [rating, setRating] = useState(0);
  const [isFavorite, setIsFavorite] = useState(false);
  const [tabValue, setTabValue] = useState(0);

  const carouselRef = useRef(null);
  const thumbnailsRef = useRef(null);
  const cartCtx = useContext(CartContext);

  useEffect(() => {
    const fetchProductDetails = async () => {
      try {
        const response = await ApiService.fetchProductByNum(productNum);
        response.data = {
          ...response.data,
          productImg: response.data.productImg.concat([
            'https://picsum.photos/400/400?random=100',
            'https://picsum.photos/400/400?random=101',
            'https://picsum.photos/400/400?random=102',
            'https://picsum.photos/400/400?random=103',
            'https://picsum.photos/400/400?random=104',
            'https://picsum.photos/400/400?random=105',
            'https://picsum.photos/400/400?random=106',
          ]),
        };
        setProduct(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching product details:', error);
        setLoading(false);
      }
    };

    fetchProductDetails();
  }, [productNum]);

  const handleSizeSelect = (size) => {
    setSelectedSize(size);
  };

  const handleScroll = () => {
    if (carouselRef.current) {
      const scrollPosition = carouselRef.current.scrollLeft;
      const imageWidth = carouselRef.current.clientWidth;
      const newIndex = Math.round(scrollPosition / imageWidth);
      setCurrentImageIndex(newIndex);
    }
  };

  useEffect(() => {
    const carouselElement = carouselRef.current;
    if (carouselElement) {
      carouselElement.addEventListener('scroll', handleScroll);
    }

    return () => {
      if (carouselElement) {
        carouselElement.removeEventListener('scroll', handleScroll);
      }
    };
  }, []);

  const scrollToImage = (index) => {
    if (carouselRef.current) {
      carouselRef.current.scrollTo({
        left: index * carouselRef.current.clientWidth,
        behavior: 'smooth',
      });
    }
    setCurrentImageIndex(index);
  };

  const handleRatingChange = (event, newValue) => {
    setRating(newValue);
  };

  const handleFavoriteClick = () => {
    setIsFavorite(!isFavorite);
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const addToCartHandler = () => {
    if (!selectedSize || !product) {
      alert('사이즈를 선택해주세요.');
      return;
    }
    cartCtx.addItem({
      id: product.productNum,
      name: product.productName,
      amount: 1,
      price: product.rprice > 0 ? product.rprice : product.oprice,
      size: selectedSize,
    });
  };

  if (loading) {
    return <Typography>로딩 중...</Typography>;
  }

  if (!product) {
    return <Typography>상품을 찾을 수 없습니다.</Typography>;
  }

  const discountPercentage = product.rprice > 0 ? Math.round(((product.oprice - product.rprice) / product.oprice) * 100) : 0;

  return (
    <>
      <Header cartIsShown={cartIsShown} setCartIsShown={setCartIsShown} onShowCart={showCartHandler} />
      <Container className={styles.container}>
        <div className={styles.productGrid}>
          <div className={styles.imageCarousel}>
            <div className={styles.mainImageContainer}>
              <IconButton className={`${styles.carouselButton} ${styles.prevButton}`} onClick={() => scrollToImage(currentImageIndex - 1)}>
                <ChevronLeft />
              </IconButton>
              <div ref={carouselRef} className={styles.mainImage}>
                {product.productImg.map((img, index) => (
                  <div key={index} className={styles.imageWrapper}>
                    <img src={img} alt={`${product.productName} - 이미지 ${index + 1}`} className={styles.image} />
                  </div>
                ))}
              </div>
              <IconButton className={`${styles.carouselButton} ${styles.nextButton}`} onClick={() => scrollToImage(currentImageIndex + 1)}>
                <ChevronRight />
              </IconButton>
            </div>
            <div ref={thumbnailsRef} className={styles.thumbnails}>
              {product.productImg.map((img, index) => (
                <div
                  key={index}
                  onClick={() => scrollToImage(index)}
                  className={`${styles.thumbnail} ${currentImageIndex === index ? styles.thumbnailActive : ''}`}
                >
                  <img src={img} alt={`Thumbnail ${index + 1}`} className={styles.thumbnailImage} />
                </div>
              ))}
            </div>
          </div>

          <div className={styles.productInfo}>
            <Typography variant="h4" component="h1" className={styles.productName}>
              {product.productName}
            </Typography>
            <Box className={styles.priceSection}>
              {product.rprice > 0 ? (
                <>
                  <Typography variant="body2" className={styles.discountPercentage}>
                    {discountPercentage}% 할인
                  </Typography>
                  <Typography variant="h5" className={styles.salePrice}>
                    {product.rprice.toLocaleString()} 원
                  </Typography>
                  <Typography variant="body2" className={styles.originalPrice}>
                    {product.oprice.toLocaleString()} 원
                  </Typography>
                </>
              ) : (
                <Typography variant="h5" className={styles.salePrice}>
                  {product.oprice.toLocaleString()} 원
                </Typography>
              )}
            </Box>

            <Typography variant="subtitle1" className={styles.colorInfo}>
              색상: {colors?.[0]}
            </Typography>

            <Typography variant="subtitle1" className={styles.sizeSelection}>
              사이즈 선택
            </Typography>
            <Box>
              {sizes.map((size) => (
                <Button
                  key={size}
                  variant={selectedSize === size ? 'contained' : 'outlined'}
                  onClick={() => handleSizeSelect(size)}
                  className={`${styles.sizeButton} ${selectedSize === size ? styles.selected : ''}`}
                >
                  {size}
                </Button>
              ))}
            </Box>
            <Button
              variant="contained"
              fullWidth
              startIcon={<ShoppingCartOutlined />}
              className={styles.addToCartButton}
              onClick={addToCartHandler}
              disabled={!selectedSize}
            >
              장바구니 추가
            </Button>
            <Typography variant="body2" className={styles.shippingInfo}>
              배송 및 반품 무료
            </Typography>

            {/* ... (나머지 코드는 그대로 유지) */}
          </div>
        </div>

        {/* ... (나머지 코드는 그대로 유지) */}
      </Container>
    </>
  );
}
