import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Grid, Typography, Button, Select, MenuItem, FormControl, InputLabel, Box, Snackbar, Alert } from '@mui/material';
import { ShoppingCart } from '@mui/icons-material';
import ApiService from '../../../../ApiService';

const ProductDetail = () => {
  const { productNum } = useParams();
  const [product, setProduct] = (useState < ProductDetail) | (null > null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = (useState < string) | (null > null);
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  useEffect(() => {
    const fetchProductDetail = async () => {
      try {
        setLoading(true);
        const response = await ApiService.fetchProductByNum(Number(productNum));
        setProduct(response.data);
      } catch (err) {
        setError('Failed to fetch product details');
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetail();
  }, [productNum]);

  const handleAddToCart = () => {
    // 장바구니에 추가하는 로직 구현
    console.log('Added to cart:', { productNum, selectedSize, selectedColor, quantity });
    setSnackbarOpen(true);
  };

  if (loading) return <Loading />;
  if (error) return <Typography color="error">{error}</Typography>;
  if (!product) return <Typography>Product not found</Typography>;

  return (
    <Container maxWidth="lg">
      <Grid container spacing={4}>
        <Grid item xs={12} md={6}>
          <Box sx={{ position: 'relative', paddingTop: '100%', overflow: 'hidden' }}>
            <img
              src={product.productImg[0]}
              alt={product.productName}
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                objectFit: 'cover',
              }}
            />
          </Box>
        </Grid>
        <Grid item xs={12} md={6}>
          <Typography variant="h4" gutterBottom>
            {product.productName}
          </Typography>
          <Typography variant="subtitle1" gutterBottom>
            브랜드: {product.productBrand}
          </Typography>
          <Typography variant="h5" gutterBottom color="primary">
            {product.rprice > 0 ? (
              <>
                <span style={{ textDecoration: 'line-through', color: 'gray', marginRight: '10px' }}>{product.oprice.toLocaleString()}원</span>
                {product.rprice.toLocaleString()}원
              </>
            ) : (
              `${product.oprice.toLocaleString()}원`
            )}
          </Typography>
          <Typography variant="body1" paragraph>
            {product.productInfo}
          </Typography>

          <FormControl fullWidth margin="normal">
            <InputLabel>사이즈</InputLabel>
            <Select value={selectedSize} onChange={(e) => setSelectedSize(e.target.value)}>
              {[...new Set(product.productDetails.map((detail) => detail.size))].map((size) => (
                <MenuItem key={size} value={size}>
                  {size}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <FormControl fullWidth margin="normal">
            <InputLabel>색상</InputLabel>
            <Select value={selectedColor} onChange={(e) => setSelectedColor(e.target.value)}>
              {[...new Set(product.productDetails.map((detail) => detail.color))].map((color) => (
                <MenuItem key={color} value={color}>
                  {color}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <FormControl fullWidth margin="normal">
            <InputLabel>수량</InputLabel>
            <Select value={quantity} onChange={(e) => setQuantity(Number(e.target.value))}>
              {[1, 2, 3, 4, 5].map((num) => (
                <MenuItem key={num} value={num}>
                  {num}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <Button variant="contained" color="primary" size="large" startIcon={<ShoppingCart />} onClick={handleAddToCart} fullWidth sx={{ mt: 2 }}>
            장바구니에 담기
          </Button>
        </Grid>
      </Grid>

      <Snackbar
        open={snackbarOpen}
        autoHideDuration={3000}
        onClose={() => setSnackbarOpen(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={() => setSnackbarOpen(false)} severity="success" sx={{ width: '100%' }}>
          상품이 장바구니에 추가되었습니다!
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default ProductDetail;
