// pages/ProductComponent.js

import React, { useState, useEffect } from 'react'
import { Button, Tabs, Tab, Typography, Box } from '@mui/material'
import StarIcon from '@mui/icons-material/Star'
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder'
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart'
import ApiService from '../../../../ApiService'
import { useParams } from 'react-router-dom'
// src/components/ProductDetailStyles.js

import styles from './css/ProductDetail.module.css';

function TabPanel(props) {
  const { children, value, index, ...other } = props

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`tabpanel-${index}`}
      aria-labelledby={`tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  )
}

export default function ProductComponent() {
  const [product, setProduct] = useState(null)
  const [loading, setLoading] = useState(true)
  const [currentImage, setCurrentImage] = useState(0)
  const { productNum } = useParams()
  const [tabIndex, setTabIndex] = useState(0)

  useEffect(() => {
    const fetchProductDetails = async () => {
      try {
        const response = await ApiService.fetchProductByNum(productNum)
        const data = response.data
        setProduct(data)
      } catch (error) {
        console.error("상품 정보를 불러오는 중 오류 발생:", error)
      } finally {
        setLoading(false)
      }
    }

    fetchProductDetails()
  }, [productNum])

  if (loading) return <div>로딩 중...</div>
  if (!product) return <div>상품을 찾을 수 없습니다.</div>

  // 할인율 계산
  const discountPercentage = product.oprice > product.rprice 
    ? Math.round(((product.oprice - product.rprice) / product.oprice) * 100) 
    : 0

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f6f3e4', padding: '16px' }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto', backgroundColor: 'white', borderRadius: '8px', boxShadow: '0px 4px 12px rgba(0, 0, 0, 0.1)', overflow: 'hidden' }}>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
          {/* 이미지 갤러리 */}
          <div style={{ width: '100%', padding: '16px', textAlign: 'center' }}>
            <img
              src={product.productImg[currentImage]}
              alt={product.productName}
              style={{ width: '100%', maxWidth: '400px', borderRadius: '8px' }}
            />
            <div style={{ display: 'flex', justifyContent: 'center', gap: '8px', marginTop: '8px' }}>
              {product.productImg.map((img, index) => (
                <img
                  key={index}
                  src={img}
                  alt={`${product.productName} ${index + 1}`}
                  onClick={() => setCurrentImage(index)}
                  style={{
                    width: '60px',
                    height: '60px',
                    borderRadius: '4px',
                    border: currentImage === index ? '2px solid #12b8de' : '2px solid transparent',
                    cursor: 'pointer'
                  }}
                />
              ))}
            </div>
          </div>

          {/* 제품 정보 */}
          <div style={{ padding: '16px' }}>
            <Typography variant="h4" component="h1" gutterBottom>{product.productName}</Typography>
            <Typography variant="subtitle1" color="textSecondary" gutterBottom>{product.productBrand}</Typography>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: '16px' }}>
              {[...Array(5)].map((_, i) => (
                <StarIcon key={i} style={{ color: i < Math.floor(product.rating) ? '#FFD700' : '#e0e0e0' }} />
              ))}
              <Typography variant="body2" color="textSecondary" style={{ marginLeft: '8px' }}>
                {product.rating} ({product.reviewCount} 리뷰)
              </Typography>
            </div>
            <div style={{ marginBottom: '16px' }}>
              <Typography variant="h5" color="primary" style={{ fontWeight: 'bold' }}>
                {product.rprice.toLocaleString()}원
              </Typography>
              {discountPercentage > 0 && (
                <>
                  <Typography variant="body1" color="textSecondary" style={{ textDecoration: 'line-through', marginLeft: '8px' }}>
                    {product.oprice.toLocaleString()}원
                  </Typography>
                  <Typography variant="body1" color="secondary" style={{ marginLeft: '8px' }}>
                    {discountPercentage}% 할인
                  </Typography>
                </>
              )}
            </div>
            <Typography variant="body2" color="textSecondary" paragraph>{product.description}</Typography>
            <div style={{ marginBottom: '16px' }}>
              <Typography variant="subtitle2">사이즈</Typography>
              <div style={{ display: 'flex', gap: '8px', marginTop: '8px' }}>
                {product.sizes.map((size) => (
                  <Button key={size} variant="outlined">{size}</Button>
                ))}
              </div>
            </div>
            <div style={{ marginBottom: '16px' }}>
              <Typography variant="subtitle2">색상</Typography>
              <div style={{ display: 'flex', gap: '8px', marginTop: '8px' }}>
                {product.colors.map((color) => (
                  <Button key={color} variant="outlined">{color}</Button>
                ))}
              </div>
            </div>
            <div style={{ display: 'flex', gap: '16px' }}>
              <Button variant="contained" color="primary" startIcon={<ShoppingCartIcon />}>
                장바구니에 담기
              </Button>
              <Button variant="outlined" color="primary" startIcon={<FavoriteBorderIcon />}>
                찜하기
              </Button>
            </div>
          </div>
        </div>

        {/* 상세 정보 탭 */}
        <Tabs value={tabIndex} onChange={(e, newIndex) => setTabIndex(newIndex)} centered style={{ marginTop: '16px' }}>
          <Tab label="상세 정보" />
          <Tab label="리뷰" />
          <Tab label="배송 및 반품" />
        </Tabs>
        <TabPanel value={tabIndex} index={0}>
          <Typography variant="h6">제품 상세</Typography>
          <ul>
            {product.productDetails.map((detail, index) => (
              <li key={index}>
                <strong>사이즈:</strong> {detail.productSize}, 재고: {detail.productInven}개
              </li>
            ))}
          </ul>
        </TabPanel>
        <TabPanel value={tabIndex} index={1}>
          <Typography variant="h6">고객 리뷰</Typography>
          {product.reviews.length > 0 ? (
            product.reviews.map((review) => (
              <div key={review.reviewNum} style={{ borderBottom: '1px solid #e0e0e0', paddingBottom: '8px', marginBottom: '8px' }}>
                <Typography variant="subtitle1">{review.reviewTitle}</Typography>
                <Typography variant="body2">{review.reviewContent}</Typography>
              </div>
            ))
          ) : (
            <Typography>아직 작성된 리뷰가 없습니다.</Typography>
          )}
        </TabPanel>
        <TabPanel value={tabIndex} index={2}>
          <Typography variant="h6">배송 및 반품 안내</Typography>
          <ul>
            <li>배송비: 3,000원 (5만원 이상 구매 시 무료배송)</li>
            <li>배송 기간: 결제 완료 후 1-3 영업일 이내 출고</li>
            <li>반품 기간: 상품 수령 후 7일 이내 (미사용 상품에 한함)</li>
            <li>교환/반품 배송비: 6,000원 (단순 변심의 경우 고객 부담)</li>
          </ul>
        </TabPanel>
      </div>
    </div>
  )
}
