import React, { useCallback, useContext, useEffect, useState, useMemo } from 'react';
import { Pagination } from '@mui/material';
import { debounce } from 'lodash';
import ApiService from '../../../ApiService';
import AuthContext from '../../../store/auth-context';
import Loading from '../../basic/Loading';
import Card from '../UI/Card';
import * as CONSTANT from './const';
import classes from './css/AvailableProducts.module.css';
import ProductForm from './productForm/ProductForm';
import ProductItem from './productItem/ProductItem';

const AvailableProducts = () => {
  const ctx = useContext(AuthContext);

  const [products, setProducts] = useState([]);
  const [query, setQuery] = useState({
    genre_major: '',
    genre_minor: '',
    genre: '',
    brand: '',
    category: '',
    sort: 'payment_n,desc',
    page: 1,
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [totalPages, setTotalPages] = useState(1);

  const fetchProductsHandler = useCallback(async (queryObj) => {
    setIsLoading(true);
    setError(null);
    try {
      let response;
      if (Object.values(queryObj).some((x) => x !== '')) {
        const queryArray = Object.entries(queryObj)
          .filter(([key, value]) => !['genre_major', 'genre_minor'].includes(key) && value !== '')
          .map(([key, value]) => `${key}=${encodeURIComponent(value)}`);
        const queryString = queryArray.join('&');
        response = await ApiService.fetchPrudctsByQueryString(queryString);
      } else {
        response = await ApiService.fetchProducts();
      }
      if (response.status < 200 || response.status >= 300) {
        throw new Error('Something went wrong!');
      }
      const data = response.data;
      setProducts(data.content);
      setTotalPages(data.totalPages);
    } catch (err) {
      setError(err.message || 'Failed to fetch products');
    } finally {
      setIsLoading(false);
    }
  }, []);

  const debouncedFetchProducts = useMemo(() => debounce(fetchProductsHandler, 300), [fetchProductsHandler]);

  useEffect(() => {
    debouncedFetchProducts(query);
    return () => debouncedFetchProducts.cancel();
  }, [debouncedFetchProducts, query]);

  useEffect(() => {
    ctx.setCurrentPage('products');
  }, [ctx]);

  const handlePageChange = useCallback((event, value) => {
    setQuery((prevQuery) => ({ ...prevQuery, page: value }));
  }, []);

  const handleFilterChange = useCallback((filterId, value) => {
    setQuery((prevQuery) => ({ ...prevQuery, [filterId]: value, page: 1 }));
  }, []);

  const memoizedProductsList = useMemo(() => {
    return products.map((product) => (
      <ProductItem
        id={product.productCode}
        num={product.productNum}
        key={product.productCode}
        name={product.productName}
        genre={product.genreCode}
        img={product.productImg[0]}
        oprice={product.oprice}
        rprice={product.rprice}
      />
    ));
  }, [products]);

  if (isLoading) {
    return (
      <Card className={classes.products}>
        <Loading />
      </Card>
    );
  }

  if (error) {
    return (
      <Card className={classes.products}>
        <div>
          <p>{error}</p>
        </div>
      </Card>
    );
  }

  if (products.length === 0) {
    return (
      <Card className={classes.products}>
        <h2>현재 상품 목록이 비어있습니다.</h2>
      </Card>
    );
  }

  return (
    <Card className={classes.products}>
      <ProductForm queryObj={query} fetchProducts={handleFilterChange} renderItems={CONSTANT.renderItems} />
      <ul className={classes['product-list']}>{memoizedProductsList}</ul>
      <Pagination count={totalPages} page={query.page} onChange={handlePageChange} color="primary" className={classes.pagination} />
    </Card>
  );
};

export default React.memo(AvailableProducts);
