import React, { useState, useEffect, useRef } from 'react';
import {
  Container,
  Typography,
  Button,
  IconButton,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Rating,
  Tabs,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Avatar,
  Chip,
  Box,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
} from '@mui/material';
import {
  ExpandMore,
  Favorite,
  FavoriteBorder,
  Share,
  Star,
  StarBorder,
  ShoppingCartOutlined,
  ThumbUpOutlined,
  VerifiedUser,
  Check,
} from '@mui/icons-material';
import Header from '../../../layout/Header';
import { TabPanel } from '../../../layout';
import styles from './css/ProductDetail.module.css';
import { dummyProduct, fabricInfo, reviews, sizeGuide } from './data';

export default function ProductDetail() {
  const [selectedSize, setSelectedSize] = useState('');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [expandedMaterial, setExpandedMaterial] = useState(true);
  const [expandedCare, setExpandedCare] = useState(false);
  const [images, setImages] = useState([]);
  const [rating, setRating] = useState(0);
  const [isFavorite, setIsFavorite] = useState(false);
  const [tabValue, setTabValue] = useState(0);
  const carouselRef = useRef(null);
  const thumbnailsRef = useRef(null);

  useEffect(() => {
    setImages(generateRandomImages(5));
  }, []);

  const generateRandomImages = (count) => {
    return Array.from({ length: count }, (_, i) => `https://picsum.photos/800/1200?random=${i}`);
  };

  const handleSizeSelect = (size) => {
    setSelectedSize(size);
  };

  const handleScroll = (e) => {
    if (carouselRef.current) {
      const scrollPosition = e.target.scrollTop;
      const imageHeight = carouselRef.current.clientHeight;
      const newIndex = Math.round(scrollPosition / imageHeight);
      setCurrentImageIndex(newIndex);

      // Scroll thumbnails
      if (thumbnailsRef.current) {
        const thumbnailWidth = thumbnailsRef.current.children[0].offsetWidth;
        thumbnailsRef.current.scrollTo({
          left: newIndex * thumbnailWidth,
          behavior: 'smooth',
        });
      }
    }
  };

  const handleMaterialAccordionChange = (event, isExpanded) => {
    setExpandedMaterial(isExpanded);
  };

  const handleCareAccordionChange = (event, isExpanded) => {
    setExpandedCare(isExpanded);
  };

  const scrollToImage = (index) => {
    if (carouselRef.current) {
      carouselRef.current.scrollTo({
        top: index * carouselRef.current.clientHeight,
        behavior: 'smooth',
      });
    }
  };

  const handleRatingChange = (event, newValue) => {
    setRating(newValue);
  };

  const handleFavoriteClick = () => {
    setIsFavorite(!isFavorite);
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  return (
    <>
      <Header />
      <Container className={styles.container}>
        <div className={styles.productGrid}>
          <div className={styles.imageCarousel}>
            <div ref={carouselRef} className={styles.mainImage} onScroll={handleScroll}>
              {images.map((img, index) => (
                <div key={index} className={styles.imageWrapper}>
                  <img src={img} alt={`${dummyProduct.name} - 이미지 ${index + 1}`} className={styles.image} />
                </div>
              ))}
            </div>
            <div ref={thumbnailsRef} className={styles.thumbnails}>
              {images.map((img, index) => (
                <div
                  key={index}
                  onClick={() => scrollToImage(index)}
                  className={`${styles.thumbnail} ${currentImageIndex === index ? styles.thumbnailActive : ''}`}
                >
                  <img src={img} alt={`Thumbnail ${index + 1}`} className={styles.thumbnailImage} />
                </div>
              ))}
            </div>
          </div>

          {/* Rest of the component remains the same */}
          {/* ... */}
        </div>
      </Container>
    </>
  );
}
