import React from "react";
import { Link } from "react-router-dom";
import styles from '.css/Home.module.css';  // 모듈 CSS 불러오기

const Home = () => {
  return (
    <div className={styles.home}>
      {/* 로그인 버튼 */}
      <div className={styles.login}>
        <Link to='/sign-in'><button>로그인</button></Link>
      </div>

      {/* 첫 번째 컨테이너 (타이틀 등) */}
      <div className={styles.container__1}>
        <h1>쇼핑몰 타이틀</h1>
        <div className={styles.title__link}>
          <Link to='/prod-list'>상품목록</Link>
        </div>
      </div>

      {/* 시계 및 기타 정보 */}
      <div className={styles.container__2}>
        <div className={styles.clock}>
          <div className={styles.clock__caldr}>2024-10-10</div>
          <div className={styles.clock__time}>10:30 AM</div>
        </div>

        {/* 리스트 */}
        <div className={styles.list}>
          <p>추천 상품</p>
          <Link to='/prod-list'>전체 상품 보기</Link>
        </div>
      </div>
    </div>
  );
}

export default Home;
