import { useContext } from "react";

import CartContext from "../../../../store/cart-context";
import MealItemForm from "./MealItemForm";
import classes from "./css/MealItem.module.css";

const MealItem = (props) => {
  const cartCtx = useContext(CartContext);
  const price = `${props.price} 원`;

  const addToCartHandler = (amount) => {
    cartCtx.addItem({
      id: props.id,
      key: props.key,
      name: props.name,
      genre: props.genre,
      img: props.img,
      amount: amount,
      price: props.price,
    });
  };

  return (
    <li className={classes.meal}>
      <div>
        <h3>{props.name}</h3>
        <div className={classes.description}>{props.genre}</div>
        <div><img src={props.img}></img></div>
        <div className={classes.price}>{price}</div>
      </div>
      <div>
        <MealItemForm id={props.id} key={props.key} onAddToCart={addToCartHandler} />
      </div>
    </li>
  );
};

export default MealItem;
