import { useContext } from 'react';
import { Link } from 'react-router-dom';

import CartContext from '../../../../store/cart-context';
import ProductItemForm from './ProductItemForm';
import classes from './css/ProductItem.module.css';

const ProductItem = (props) => {
	const cartCtx = useContext(CartContext);
	const price = `${props.price} 원`;

	const addToCartHandler = (amount) => {
		cartCtx.addItem({
			id: props.id,
			name: props.name,
			amount: amount,
			price: props.price,
		});
	};

	// 원가와 할인 가격 계산
	const originalPrice = props.oprice;
	const discountedPrice = props.rprice;
	const discountPercentage = originalPrice > 0 ? Math.round(((originalPrice - discountedPrice) / originalPrice) * 100) : 0;

	return (
		<li className={classes.product}>
			<div>
				<h3>{props.name}</h3>
				<div className={classes.description}>{props.genre}</div>
				<div>
					<Link>
						<img src={props.img} alt={props.name} />
					</Link>
				</div>
				<div className={classes.price}>
					{/* 정가 표시 */}
					<div className={classes.original-price}>
						{originalPrice.toLocaleString()} 원
					</div>
					{/* 할인율과 할인가 표시 */}
					<div className={classes.discount-info}>
						<span className={classes.discount-percentage}>{discountPercentage}%</span>
						<span>{discountedPrice > 0 ? discountedPrice.toLocaleString() + ' 원' : ''}</span>
					</div>
				</div>
			</div>
			<div>
				<ProductItemForm id={props.id} onAddToCart={addToCartHandler} />
			</div>
		</li>
	);
};

export default ProductItem;
