import { FormControl, InputLabel, MenuItem, Select } from '@material-ui/core';
import React, { useState } from 'react';

const ProductFilter = (props = { filter_name, menu_list }) => {
	const [filterMethod, setFilterMethod] = useState('');

	const onSelectChange = (event) => {
		setFilterMethod(event.target.value);

		if (event.target.value && event.target.value !== '') {
			fetchProductsHandlerByGenreCode(event.target.value);
		}
	};

	return (
		<FormControl variant="filled" fullWidth>
			<InputLabel htmlFor="uncontrolled-native">{props.filter_name}</InputLabel>
			<Select displayEmpty value={filterMethod} onChange={onSelectChange} sx={{ textAlign: 'right' }}>
				<MenuItem value="">전체</MenuItem>
				{props.menu_list?.map((menu_item) => {
					<MenuItem value={menu_item.value}>{menu_item.label}</MenuItem>;
				})}
			</Select>
		</FormControl>
	);
};

export default ProductFilter;
