import React, { useState, useEffect, useRef, useContext, memo, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Typography,
  Button,
  IconButton,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Rating,
  Tabs,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Avatar,
  Chip,
  Box,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
} from '@mui/material';
import {
  ExpandMore,
  Favorite,
  FavoriteBorder,
  Share,
  Star,
  StarBorder,
  ShoppingCartOutlined,
  ThumbUpOutlined,
  VerifiedUser,
  Check,
  Wash,
  RemoveCircleOutline,
  Iron,
  DryCleaningOutlined,
} from '@mui/icons-material';
import { TabPanel } from '../../../layout';
import Header from '../../../layout/Header';
import styles from './css/ProductDetail.module.css';
import ApiService from '../../../../ApiService';
import CartContext from '../../../../store/cart-context';
import { care, colors, details, fabricInfo, features, reviews, sizeChart, sizeGuide, sizes } from './data';

const _ProductDetail = function ProductDetail() {
  // ... (previous state declarations remain the same)

  const handleRatingChange = (event, newValue) => {
    setRating(newValue);
  };

  // ... (other functions remain the same)

  return (
    <>
      <Header cartIsShown={cartIsShown} setCartIsShown={setCartIsShown} onShowCart={showCartHandler} />
      <Container className={styles.container}>
        <div className={styles.productGrid}>
          {/* ... (image carousel remains the same) */}

          <div className={styles.productInfo}>
            {/* ... (product info remains the same) */}

            <Typography variant="subtitle1" className={styles.sizeSelection}>
              사이즈 선택
            </Typography>
            <Box className={styles.sizeButtonContainer}>
              {sizes.map((size) => (
                <Button
                  key={size}
                  variant={selectedSize === size ? 'contained' : 'outlined'}
                  onClick={() => handleSizeSelect(size)}
                  className={`${styles.sizeButton} ${selectedSize === size ? styles.selected : ''}`}
                >
                  {size}
                </Button>
              ))}
            </Box>

            {/* ... (add to cart button and shipping info remain the same) */}

            <Box className={styles.ratingSection}>
              <Typography variant="subtitle1" gutterBottom>
                상품 평가
              </Typography>
              <Box display="flex" alignItems="center">
                <Rating
                  name="product-rating"
                  value={rating}
                  precision={0.5}
                  onChange={handleRatingChange}
                  emptyIcon={<StarBorder style={{ opacity: 0.55 }} fontSize="inherit" />}
                  icon={<Star fontSize="inherit" />}
                />
                <Typography variant="body2" ml={1}>
                  {rating ? `${rating * 2}/10` : '0/10'}
                </Typography>
              </Box>
            </Box>

            {/* ... (rest of the component remains the same) */}
          </div>
        </div>

        {/* ... (tabs and reviews section remain the same) */}
      </Container>
    </>
  );
};

export default memo(_ProductDetail);
