import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styles from './Home.module.css';  // 모듈 CSS 불러오기

const Home = () => {
  const [currentTime, setCurrentTime] = useState(new Date());

  // 시계가 실시간으로 업데이트되도록 useEffect 사용
  useEffect(() => {
    const timerId = setInterval(() => setCurrentTime(new Date()), 1000);

    // 컴포넌트가 언마운트될 때 타이머 정리
    return () => clearInterval(timerId);
  }, []);

  // 시간과 날짜 형식
  const formattedTime = currentTime.toLocaleTimeString();
  const formattedDate = currentTime.toLocaleDateString();

  return (
    <div className={styles.homeContainer}>
      {/* 로그인 버튼 */}
      <div className={styles.login}>
        <Link to='/sign-in'><button>로그인</button></Link>
      </div>

      {/* 배너 섹션 */}
      <div className={styles.banner}>
        {/* 배너 이미지 */}
      </div>

      {/* 카테고리 메뉴 */}
      <div className={styles.categoryMenu}>
        <div>남성 의류</div>
        <div>여성 의류</div>
        <div>액세서리</div>
      </div>

      {/* 추천 상품 섹션 */}
      <div className={styles.featuredProducts}>
        <h2>추천 상품</h2>
        <div className={styles.productList}>
          <div className={styles.productItem}>
            <img src="상품이미지URL1" alt="상품1" />
            <div>상품 이름 1</div>
            <div>₩10,000</div>
            <div>₩15,000</div>
          </div>
          <div className={styles.productItem}>
            <img src="상품이미지URL2" alt="상품2" />
            <div>상품 이름 2</div>
            <div>₩20,000</div>
            <div>₩25,000</div>
          </div>
        </div>
      </div>

      {/* 시계 섹션 */}
      <div className={styles.clock}>
        <div className={styles.clock__caldr}>{formattedDate}</div>
        <div className={styles.clock__time}>{formattedTime}</div>
      </div>
    </div>
  );
}

export default Home;
