import React, { useState, useEffect, useRef } from 'react';
import {
  Container,
  Typography,
  Button,
  IconButton,
  Breadcrumbs,
  Link,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Rating,
  Tabs,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Avatar,
  Chip,
  Box,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
} from '@mui/material';
import {
  ExpandMore,
  Favorite,
  FavoriteBorder,
  Share,
  Star,
  StarBorder,
  ShoppingCartOutlined,
  ThumbUp,
  ThumbUpOutlined,
  VerifiedUser,
  Check,
  LocalLaundryService,
  Iron,
  DryCleaningOutlined,
  RemoveCircleOutline,
} from '@mui/icons-material';
import Image from 'next/image';
import styles from './ProductDetail.module.css';

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div role="tabpanel" hidden={value !== index} id={`simple-tabpanel-${index}`} aria-labelledby={`simple-tab-${index}`} {...other}>
      {value === index && (
        <div className={styles.tabPanel}>
          <Typography>{children}</Typography>
        </div>
      )}
    </div>
  );
}

export default function ProductDetail() {
  const [selectedSize, setSelectedSize] = useState('');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [expandedMaterial, setExpandedMaterial] = useState(true);
  const [expandedCare, setExpandedCare] = useState(false);
  const [images, setImages] = useState([]);
  const [rating, setRating] = useState(0);
  const [isFavorite, setIsFavorite] = useState(false);
  const [tabValue, setTabValue] = useState(0);
  const carouselRef = useRef(null);

  const dummyProduct = {
    name: "플리츠 스트레이트 팬츠",
    price: "59,000 원",
    colors: ["블랙", "화이트", "베이지"],
    sizes: ["XS", "S", "M", "L", "XL"],
    description: "하이웨이스트 팬츠. 와이드 레그. 앞면 플리츠 디테일. 옆면 포켓. 뒷면 파이핑 포켓. 앞면 지퍼, 내부 버튼 및 금속 후크 여밈.",
    details: [
      "겉감",
      "63% 폴리에스터 · 34% 비스코스 · 3% 엘라스탄"
    ],
    care: [
      "최대 30°C에서 약하게 세탁하기",
      "염소 표백 불가",
      "최대 110°C에서 다림질하기",
      "드라이클리닝 불가",
      "건조기 사용 불가"
    ],
    features: [
      "하이웨이스트 디자인",
      "와이드 레그 실루엣",
      "앞면 플리츠 디테일",
      "옆면 포켓",
      "뒷면 파이핑 포켓",
      "앞면 지퍼, 내부 버튼 및 금속 후크 여밈"
    ],
    sizeChart: [
      { size: 'XS', waist: '64cm', hip: '88cm', length: '100cm' },
      { size: 'S', waist: '68cm', hip: '92cm', length: '101cm' },
      { size: 'M', waist: '72cm', hip: '96cm', length: '102cm' },
      { size: 'L', waist: '76cm', hip: '100cm', length: '103cm' },
      { size: 'XL', waist: '80cm', hip: '104cm', length: '104cm' },
    ]
  };

  const fabricInfo = {
    main: "메인 원단: 폴리에스터 63%, 레이온 34%, 스판덱스 3%",
    details: [
      "신축성 있는 소재로 편안한 착용감",
      "구김이 적고 관리가 용이함",
      "사계절 착용 가능한 적당한 두께감"
    ],
    care: [
      { icon: <LocalLaundryService />, text: "찬물 또는 30도 이하 물에서 단독세탁" },
      { icon: <RemoveCircleOutline />, text: "표백제 사용 금지" },
      { icon: <Iron />, text: "낮은 온도에서 다림질" },
      { icon: <DryCleaningOutlined />, text: "드라이클리닝 권장" }
    ]
  };

  const sizeGuide = {
    measurements: [
      { label: "허리단면", description: "바지의 허리를 일자로 잰 길이의 1/2" },
      { label: "힙단면", description: "엉덩이 가장 넓은 부분의 1/2" },
      { label: "밑위", description: "허리 상단부터 밑아래 봉제선까지의 수직거리" },
      { label: "허벅지단면", description: "허벅지 가장 넓은 부분의 1/2" },
      { label: "총장", description: "허리 상단부터 밑단까지의 수직거리" },
      { label: "밑단", description: "바지 끝단의 단면" }
    ]
  };

  const reviews = {
    average: 4.8,
    total: 245,
    bestReviews: [
      {
        id: 1,
        user: "패션좋아",
        rating: 5,
        content: "허리가 너무 편하고 다리가 길어보여요! 오피스룩으로 딱이에요",
        images: [
          "https://picsum.photos/400/400?random=1",
          "https://picsum.photos/400/400?random=2"
        ],
        helpful: 42,
        verified: true,
        date: "2024.01.15",
        size: "S",
        color: "블랙",
        height: "163cm"
      },
      {
        id: 2,
        user: "스타일리시",
        rating: 5,
        content: "핏이 너무 예쁘고 소재도 좋아요. 여름에도 입을 수 있을 것 같아요!",
        images: [
          "https://picsum.photos/400/400?random=3"
        ],
        helpful: 38,
        verified: true,
        date: "2024.01.14",
        size: "M",
        color: "베이지",
        height: "168cm"
      }
    ],
    reviews: [
      {
        id: 3,
        user: "데일리룩",
        rating: 4,
        content: "전체적으로 만족스러워요. 다만 생각보다 기장이 조금 긴 감이 있어요.",
        images: [],
        helpful: 15,
        verified: true,
        date: "2024.01.13",
        size: "S",
        color: "블랙",
        height: "160cm"
      },
      {
        id: 4,
        user: "쇼핑러버",
        rating: 5,
        content: "일주일 정도 입어봤는데 정말 편하고 예뻐요! 블랙 색상이라 어디에나 잘 어울려요.",
        images: [
          "https://picsum.photos/400/400?random=4",
          "https://picsum.photos/400/400?random=5",
          "https://picsum.photos/400/400?random=6"
        ],
        helpful: 27,
        verified: true,
        date: "2024.01.12",
        size: "M",
        color: "블랙",
        height: "165cm"
      },
    ]
  };

  useEffect(() => {
    setImages(generateRandomImages(5));
  }, []);

  const generateRandomImages = (count) => {
    return Array.from({ length: count }, (_, i) => `https://picsum.photos/800/1200?random=${i}`);
  };

  const handleSizeSelect = (size) => {
    setSelectedSize(size);
  };

  const handleScroll = () => {
    if (carouselRef.current) {
      const scrollPosition = carouselRef.current.scrollLeft;
      const imageWidth = carouselRef.current.clientWidth;
      const newIndex = Math.round(scrollPosition / imageWidth);
      setCurrentImageIndex(newIndex);
    }
  };

  const handleMaterialAccordionChange = (event, isExpanded) => {
    setExpandedMaterial(isExpanded);
  };

  const handleCareAccordionChange = (event, isExpanded) => {
    setExpandedCare(isExpanded);
  };

  const scrollToImage = (index) => {
    if (carouselRef.current) {
      carouselRef.current.scrollTo({
        left: index * carouselRef.current.clientWidth,
        behavior: 'smooth'
      });
    }
  };

  const handleRatingChange = (event, newValue) => {
    setRating(newValue);
  };

  const handleFavoriteClick = () => {
    setIsFavorite(!isFavorite);
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  return (
    <Container className={styles.container}>
      <div className={styles.productGrid}>
        <div className={styles.imageCarousel}>
          <div ref={carouselRef} className={styles.mainImage} onScroll={handleScroll}>
            {images.map((img, index) => (
              <div key={index} className={styles.imageWrapper}>
                <img
                  src={img}
                  alt={`${dummyProduct.name} - 이미지 ${index + 1}`}
                  className={styles.image}
                />
              </div>
            ))}
          </div>
          <div className={styles.thumbnails}>
            {images.map((img, index) => (
              <div
                key={index}
                onClick={() => scrollToImage(index)}
                className={`${styles.thumbnail} ${currentImageIndex === index ? styles.thumbnailActive : ''}`}
              >
                <img src={img} alt={`Thumbnail ${index + 1}`} className={styles.thumbnailImage} />
              </div>
            ))}
          </div>
        </div>

        <div className={styles.productInfo}>
          <Typography variant="h4" component="h1" className={styles.productName}>
            {dummyProduct.name}
          </Typography>
          <Typography variant="h5" className={styles.productPrice}>
            {dummyProduct.price}
          </Typography>

          <Typography variant="subtitle1" className={styles.colorInfo}>
            색상: {dummyProduct.colors[0]}
          </Typography>

          <Typography variant="subtitle1" className={styles.sizeSelection}>
            사이즈 선택
          </Typography>
          <div>
            {dummyProduct.sizes.map((size) => (
              <Button
                key={size}
                variant={selectedSize === size ? "contained" : "outlined"}
                onClick={() => handleSizeSelect(size)}
                className={styles.sizeButton}
              >
                {size}
              </Button>
            ))}
          </div>

          <Button
            variant="contained"
            fullWidth
            startIcon={<ShoppingCartOutlined />}
            className={styles.addToCartButton}
          >
            장바구니 추가
          </Button>

          <Typography variant="body2" className={styles.shippingInfo}>
            배송 및 반품 무료
          </Typography>

          <div className={styles.ratingSection}>
            <Typography variant="subtitle1" gutterBottom>
              상품 평가
            </Typography>
            <Rating
              name="product-rating"
              value={rating}
              precision={0.5}
              onChange={handleRatingChange}
              emptyIcon={<StarBorder style={{ opacity: 0.55 }} fontSize="inherit" />}
              icon={<Star fontSize="inherit" />}
            />
          </div>

          <div className={styles.iconButtons}>
            <IconButton onClick={handleFavoriteClick}>
              {isFavorite ? (
                <Favorite style={{ color: '#fd6155' }} />
              ) : (
                <FavoriteBorder />
              )}
            </IconButton>
            <IconButton>
              <Share />
            </IconButton>
          </div>

          <Accordion expanded={expandedMaterial} onChange={handleMaterialAccordionChange} className={styles.accordion}>
            <AccordionSummary expandIcon={<ExpandMore />}>
              <Typography>소재</Typography>
            </AccordionSummary>
            <AccordionDetails>
              <Typography>
                {dummyProduct.details.join('\n')}
              </Typography>
            </AccordionDetails>
          </Accordion>

          <Accordion expanded={expandedCare} onChange={handleCareAccordionChange} className={styles.accordion}>
            <AccordionSummary expandIcon={<ExpandMore />}>
              <Typography>관리</Typography>
            </AccordionSummary>
            <AccordionDetails>
              <ul>
                {dummyProduct.care.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>
            </AccordionDetails>
          </Accordion>
        </div>
      </div>

      <div className={styles.tabsContainer}>
        <Tabs value={tabValue} onChange={handleTabChange} aria-label="product info tabs">
          <Tab label="상세설명" />
          <Tab label="특징" />
          <Tab label="리뷰" />
        </Tabs>

        <TabPanel value={tabValue} index={0}>
          <Box className={styles.detailContent}>
            {/* Size Measurement Diagram */}
            <Typography variant="h6" gutterBottom>사이즈 측정 방법</Typography>
            <Box className={styles.sizeGuideImage}>
              <Image
                src="https://hebbkx1anhila5yf.public.blob.vercel-storage.com/%EC%82%AC%EC%9D%B4%EC%A6%88%20%EB%8B%A8%EB%A9%B4%20%EA%B7%B8%EB%A6%BC-bOlRVk093K9JaTwVL123dsrEQaiita.png"
                alt="사이즈 측정 방법"
                width={400}
                height={500}
                style={{ objectFit: 'contain' }}
              />
            </Box>

            <List>
              {sizeGuide.measurements.map((item, index) => (
                <ListItem key={index}>
                  <ListItemText 
                    primary={item.label}
                    secondary={item.description}
                  />
                </ListItem>
              ))}
            </List>

            {/* Size Chart */}
            <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>사이즈 정보</Typography>
            <TableContainer component={Paper}>
              <Table aria-label="size chart">
                <TableHead>
                  <TableRow>
                    <TableCell>사이즈</TableCell>
                    <TableCell align="right">허리단면</TableCell>
                    <TableCell align="right">힙단면</TableCell>
                    <TableCell align="right">밑위</TableCell>
                    <TableCell align="right">허벅지단면</TableCell>
                    <TableCell align="right">총장</TableCell>
                    <TableCell align="right">밑단</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {dummyProduct.sizeChart.map((row) => (
                    <TableRow key={row.size}>
                      <TableCell component="th" scope="row">{row.size}</TableCell>
                      <TableCell align="right">{row.waist}</TableCell>
                      <TableCell align="right">{row.hip}</TableCell>
                      <TableCell align="right">27cm</TableCell>
                      <TableCell align="right">30cm</TableCell>
                      <TableCell align="right">{row.length}</TableCell>
                      <TableCell align="right">22cm</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>

            {/* Fabric Information */}
            <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>원단 정보</Typography>
            <Typography paragraph>{fabricInfo.main}</Typography>
            <List>
              {fabricInfo.details.map((detail, index) => (
                <ListItem key={index}>
                  <ListItemIcon>
                    <Check />
                  </ListItemIcon>
                  <ListItemText primary={detail} />
                </ListItem>
              ))}
            </List>

            {/* Care Instructions */}
            <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>세탁 및 관리</Typography>
            <List>
              {fabricInfo.care.map((instruction, index) => (
                <ListItem key={index}>
                  <ListItemIcon>
                    {instruction.icon}
                  </ListItemIcon>
                  <ListItemText primary={instruction.text} />
                </ListItem>
              ))}
            </List>
          </Box>
        </TabPanel>

        <TabPanel value={tabValue} index={1}>
          <Typography variant="h6" gutterBottom>제품 특징</Typography>
          <ul>
            {dummyProduct.features.map((feature, index) => (
              <li key={index}>{feature}</li>
            ))}
          </ul>
        </TabPanel>

        <TabPanel value={tabValue} index={2}>
          <div className={styles.reviewSection}>
            <div className={styles.reviewHeader}>
              <Typography variant="h5">리뷰 {reviews.total}</Typography>
              <div className={styles.averageRating}>{reviews.average}</div>
              <Rating value={reviews.average} precision={0.1} readOnly size="large" />
            </div>
          </div>

          <div className={styles.bestReviews}>
            <Typography variant="h6" gutterBottom>
              베스트 리뷰
            </Typography>
            {reviews.bestReviews.map((review) => (
              <div key={review.id} className={styles.reviewCard}>
                <div className={styles.reviewUser}>
                  <Avatar sx={{ width: 24, height: 24 }}>{review.user[0]}</Avatar>
                  <Typography variant="subtitle2">{review.user}</Typography>
                  {review.verified && (
                    <VerifiedUser sx={{ width: 16, height: 16, color: 'primary.main' }} />
                  )}
                </div>
                <Rating value={review.rating} size="small" readOnly />
                <Typography variant="body2" className={styles.reviewContent}>
                  {review.content}
                </Typography>
                {review.images.length > 0 && (
                  <div className={styles.reviewImages}>
                    {review.images.map((img, index) => (
                      <img
                        key={index}
                        src={img}
                        alt={`리뷰 이미지 ${index + 1}`}
                        className={styles.reviewImage}
                      />
                    ))}
                  </div>
                )}
                <div className={styles.reviewMeta}>
                  <div>
                    <Chip size="small" label={`${review.size} 구매`} />
                    <Chip size="small" label={review.height} />
                  </div>
                  <div className={styles.helpfulButton}>
                    <ThumbUpOutlined sx={{ width: 16, height: 16 }} />
                    <Typography variant="caption">{review.helpful}</Typography>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className={styles.reviewPhotos}>
            <Typography variant="h6" gutterBottom>
              리뷰 사진
            </Typography>
            <div className={styles.photoGrid}>
              {reviews.reviews
                .filter(review => review.images.length > 0)
                .flatMap(review => review.images)
                .map((img, index) => (
                  <div key={index} className={styles.photoGridItem}>
                    <img
                      src={img}
                      alt={`리뷰 이미지 ${index + 1}`}
                      className={styles.photoGridImage}
                    />
                  </div>
                ))}
            </div>
          </div>

          <div>
            <Typography variant="h6" gutterBottom>
              전체 리뷰
            </Typography>
            {reviews.reviews.map((review) => (
              <div key={review.id} className={styles.reviewCard}>
                <div className={styles.reviewUser}>
                  <Avatar sx={{ width: 24, height: 24 }}>{review.user[0]}</Avatar>
                  <Typography variant="subtitle2">{review.user}</Typography>
                  {review.verified && (
                    <VerifiedUser sx={{ width: 16, height: 16, color: 'primary.main' }} />
                  )}
                  <Typography variant="caption" sx={{ ml: 'auto' }}>
                    {review.date}
                  </Typography>
                </div>
                <Rating value={review.rating} size="small" readOnly />
                <Typography variant="body2" className={styles.reviewContent}>
                  {review.content}
                </Typography>
                {review.images.length > 0 && (
                  <div className={styles.reviewImages}>
                    {review.images.map((img, index) => (
                      <img
                        key={index}
                        src={img}
                        alt={`리뷰 이미지 ${index + 1}`}
                        className={styles.reviewImage}
                      />
                    ))}
                  </div>
                )}
                <div className={styles.reviewMeta}>
                  <div>
                    <Chip size="small" label={`${review.size} 구매`} />
                    <Chip size="small" label={review.height} />
                  </div>
                  <div className={styles.helpfulButton}>
                    <ThumbUpOutlined sx={{ width: 16, height: 16 }} />
                    <Typography variant="caption">{review.helpful}</Typography>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </TabPanel>
      </div>
    </Container>
  );
}
