import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import { useHistory } from 'react-router-dom';
import swal from 'sweetalert';
import { prdActions } from '../redux/modules/product'; // Adjust the import path as needed
import classes from './css/ProductItem.module.css';

const ProductDetail = (props) => {
    const id = props.match.params.id;
    const dispatch = useDispatch();
    const history = useHistory();
    const prd_list = useSelector((state) => state.prd.list);
    const prd_idx = prd_list.findIndex((p) => p.goodsId == id);
    const prd = prd_list[prd_idx];

    // Fetch product data if not available
    useEffect(() => {
        if (prd) return;
        dispatch(prdActions.getOnePostDB(id));
    }, [prd, id, dispatch]);

    const [options, setOption] = useState([]);
    const [sum, setSum] = useState(0);

    // Handle option selection
    const selectOption = (e) => {
        const selectedOption = e.target.value;
        const price = Number(selectedOption.slice(-7, -1).split(',').join('').trim());

        setOption((prevOptions) => [
            ...prevOptions.filter((o) => o.text !== selectedOption),
            { text: selectedOption, num: 1, prd: prd, sum: price },
        ]);
        setSum((prevSum) => prevSum + price);
    };

    // Remove selected option
    const deleteOption = (target) => {
        setOption((prevOptions) =>
            prevOptions.filter((option) => {
                if (option.text === target.text) {
                    setSum((prevSum) => prevSum - Number(target.text.slice(-7, -1).split(',').join('').trim()) * option.num);
                    return false; // Remove the option
                }
                return true; // Keep the option
            })
        );
    };

    // Change option quantity
    const changeQuantity = (option, delta) => {
        const price = Number(option.text.slice(-7, -1).split(',').join('').trim());
        setSum((prevSum) => prevSum + price * delta);
        option.num += delta;
        option.sum += price * delta;
    };

    // Save options to local storage and show alert
    const setLocalStorage = () => {
        const _cart = localStorage.getItem('cart');
        const newCart = _cart ? [...JSON.parse(_cart), ...options] : options;
        localStorage.setItem('cart', JSON.stringify(newCart));

        swal({
            title: 'Added to cart!',
            icon: 'success',
            buttons: {
                showCart: { text: 'Go to cart', value: 'showCart' },
                cancel: 'Continue shopping',
            },
        }).then((value) => {
            if (value === 'showCart') {
                history.push('/cart');
            }
        });
    };

    return (
        <React.Fragment>
            {prd && (
                <Container>
                    <ProductWrapper>
                        <Image src={prd.prd_img} alt={prd.prd_name} />
                        <Details>
                            <Title>{prd.prd_name}</Title>
                            <Subtitle>{prd.prd_sum}</Subtitle>
                            <ShippingInfo>
                                {prd.free_ship ? 'Free Shipping' : `${prd.ship} - ${prd.ship_info}`}
                            </ShippingInfo>

                            <OptionSelect onChange={selectOption}>
                                <option value="">[Required] Please select an option</option>
                                <option disabled>------------------</option>
                                {prd.option.map((opt, idx) => (
                                    <option value={opt} key={prd._id + idx}>
                                        {opt}
                                    </option>
                                ))}
                            </OptionSelect>

                            {options.map((option, idx) => (
                                <Option key={idx}>
                                    <OptionText>{option.text}</OptionText>
                                    <QuantityControl>
                                        <Button onClick={() => changeQuantity(option, -1)}>-</Button>
                                        <Quantity>{option.num}</Quantity>
                                        <Button onClick={() => changeQuantity(option, 1)}>+</Button>
                                    </QuantityControl>
                                    <RemoveButton onClick={() => deleteOption(option)}>Remove</RemoveButton>
                                </Option>
                            ))}

                            <Total>Subtotal: {sum.toLocaleString()}Ïõê</Total>
                            <Button onClick={setLocalStorage}>Add to Cart</Button>
                        </Details>
                    </ProductWrapper>

                    <ImageGallery>
                        {prd.desc.map((img, idx) => (
                            <GalleryImage key={idx} src={img} />
                        ))}
                    </ImageGallery>
                </Container>
            )}
        </React.Fragment>
    );
};

// Styled Components
const Container = styled.div`
    padding: 60px 0;
    max-width: 950px;
    margin: 0 auto;
`;

const ProductWrapper = styled.div`
    display: flex;
    margin-bottom: 30px;
`;

const Image = styled.img`
    height: 406px;
    margin-right: 20px;
`;

const Details = styled.div`
    flex: 1;
`;

const Title = styled.h1`
    font-size: 24px;
    margin-bottom: 8px;
`;

const Subtitle = styled.h2`
    font-size: 16px;
    margin-bottom: 16px;
`;

const ShippingInfo = styled.p`
    font-size: 14px;
    margin-bottom: 20px;
`;

const OptionSelect = styled.select`
    width: 100%;
    height: 40px;
    border: 1px solid #ececec;
    padding-left: 12px;
    margin-bottom: 20px;
`;

const Option = styled.div`
    display: flex;
    align-items: center;
    background-color: rgb(248, 248, 248);
    padding: 16px;
    margin-bottom: 10px;
    border-radius: 4px;
`;

const OptionText = styled.span`
    flex: 1;
    font-size: 13px;
`;

const QuantityControl = styled.div`
    display: flex;
    align-items: center;
    margin-left: 10px;
`;

const Button = styled.button`
    border: 1px solid #ccc;
    background: none;
    padding: 5px 10px;
    cursor: pointer;
`;

const Quantity = styled.span`
    margin: 0 10px;
`;

const RemoveButton = styled.button`
    border: none;
    background: none;
    color: red;
    cursor: pointer;
`;

const Total = styled.h3`
    margin-top: 20px;
`;

const ImageGallery = styled.div`
    margin-top: 30px;
`;

const GalleryImage = styled.img`
    width: 100%;
    margin-bottom: 10px;
`;

export default ProductDetail;
