import React, { useEffect, useState, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { 
  AppBar, 
  Toolbar, 
  Typography, 
  Button, 
  IconButton, 
  InputBase, 
  Card, 
  CardContent, 
  CardMedia, 
  Grid, 
  Container,
  Box
} from '@mui/material';
import { ShoppingCart, Search, Menu } from '@mui/icons-material';
import ApiService from '../../ApiService';
import closetImage from '../../assets/closet.jpg';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchProductsHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await ApiService.fetchProducts();
      if (response.status < 200 || response.status >= 300) {
        throw new Error('상품을 불러오는 데 실패했습니다.');
      }
      const data = response.data?.content || [];

      const sortedProducts = data
        .filter((product) => product.judge !== 'NULL')
        .sort((a, b) => b.judge - a.judge)
        .slice(0, 3);

      setFeaturedProducts(sortedProducts.length > 0 ? sortedProducts : data.slice(0, 3));
    } catch (err) {
      setError(err.message || '상품을 불러오는 데 실패했습니다.');
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchProductsHandler();
  }, [fetchProductsHandler]);

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" color="default">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Otfficial
          </Typography>
          <Button color="inherit" component={Link} to="/">홈</Button>
          <Button color="inherit" component={Link} to="/products">제품</Button>
          <Button color="inherit" component={Link} to="/about">소개</Button>
          <Button color="inherit" component={Link} to="/contact">문의</Button>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <InputBase
              placeholder="검색..."
              inputProps={{ 'aria-label': '검색' }}
            />
            <IconButton type="submit" aria-label="검색">
              <Search />
            </IconButton>
          </Box>
          <IconButton color="inherit" aria-label="장바구니">
            <ShoppingCart />
          </IconButton>
          <IconButton color="inherit" aria-label="메뉴" sx={{ display: { sm: 'none' } }}>
            <Menu />
          </IconButton>
        </Toolbar>
      </AppBar>

      <Box
        sx={{
          position: 'relative',
          height: '500px',
          backgroundImage: `url(${closetImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          color: 'white',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0,0,0,0.5)',
          }
        }}
      >
        <Box sx={{ position: 'relative', textAlign: 'center' }}>
          <Typography variant="h2" component="h1" gutterBottom>
            옷피셜. 세상에 없던 AI 쇼핑몰
          </Typography>
          <Button variant="contained" size="large">
            지금 쇼핑하기
          </Button>
        </Box>
      </Box>

      <Container sx={{ py: 8 }} maxWidth="lg">
        <Typography variant="h4" component="h2" gutterBottom align="center">
          추천 상품
        </Typography>
        {isLoading && <Typography>로딩 중...</Typography>}
        {error && <Typography color="error">{error}</Typography>}
        {!isLoading && !error && (
          <Grid container spacing={4}>
            {featuredProducts.map((product) => (
              <Grid item key={product.productCode} xs={12} sm={6} md={4}>
                <Card>
                  <CardMedia
                    component="img"
                    height="140"
                    image={product.productImg[0]}
                    alt={product.productName}
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      {product.productName}
                    </Typography>
                    {product.rprice > 0 ? (
                      <>
                        <Typography variant="body2" color="text.secondary" sx={{ textDecoration: 'line-through' }}>
                          {product.oprice.toLocaleString()} 원
                        </Typography>
                        <Typography variant="body2" color="error">
                          {Math.round(((product.oprice - product.rprice) / product.oprice) * 100)}% 할인
                        </Typography>
                        <Typography variant="h6">
                          {product.rprice.toLocaleString()} 원
                        </Typography>
                      </>
                    ) : (
                      <Typography variant="h6">
                        {product.oprice.toLocaleString()} 원
                      </Typography>
                    )}
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Container>

      <Box component="footer" sx={{ bgcolor: 'background.paper', py: 6 }}>
        <Container maxWidth="lg">
          <Typography variant="h6" align="center" gutterBottom>
            Otfficial
          </Typography>
          <Typography variant="subtitle1" align="center" color="text.secondary" component="p">
            © 2024 옷피셜. All rights reserved.
          </Typography>
        </Container>
      </Box>
    </Box>
  );
};

export default Home;
